DROP TABLE IF EXISTS [Smurf.Comment]
DROP TABLE IF EXISTS [Smurf.Sign]
DROP TABLE IF EXISTS [Smurf.Response]
DROP TABLE IF EXISTS [Smurf.Review]
DROP TABLE IF EXISTS [Smurf.Resident]
DROP TABLE IF EXISTS [Smurf.Reviewer]
DROP TABLE IF EXISTS [Smurf.Lease]
DROP TABLE IF EXISTS [Smurf.OwnerPhone]
DROP TABLE IF EXISTS [Smurf.UnitUtilities]
DROP TABLE IF EXISTS [Smurf.Unit]
DROP TABLE IF EXISTS [Smurf.Owner]


CREATE TABLE [Smurf.Owner](
	ownerId CHAR(3) NOT NULL,
	ownerFName VARCHAR(50),
	ownerMInit VARCHAR(20),
	ownerLName VARCHAR(20),
	CONSTRAINT PK_Owner_ownerId PRIMARY KEY (ownerId)
)


CREATE TABLE [Smurf.Unit](
	unitId CHAR(3) NOT NULL,
	unitName VARCHAR(50),
	unitStreet VARCHAR(30),
	unitCity VARCHAR(30),
	unitState CHAR(2),
	unitZipCode VARCHAR(50),
	unitType VARCHAR(30),
	unitDistance DECIMAL(4,2), 
	ownerId CHAR(3),
	CONSTRAINT PK_Unit_unitId PRIMARY KEY (unitId),
	CONSTRAINT FK_Unit_ownerId FOREIGN KEY (ownerId)
			REFERENCES [Smurf.Owner] (ownerId)
			ON DELETE CASCADE ON UPDATE CASCADE);


CREATE TABLE [Smurf.UnitUtilities](
	unitId CHAR(3) NOT NULL,
	unitUtilities VARCHAR(30) NOT NULL,
	CONSTRAINT PK_UnitUtilities_unitId_unitUtilities PRIMARY KEY (unitId, unitUtilities),
	CONSTRAINT FK_UnitUtilities_unitId FOREIGN KEY (unitId)
			REFERENCES [Smurf.Unit] (unitId)
			ON DELETE CASCADE ON UPDATE CASCADE);


CREATE TABLE [Smurf.OwnerPhone](
	ownerId CHAR(3) NOT NULL,
	ownerPhone CHAR(10) NOT NULL,
	CONSTRAINT PK_OwnerPhone_ownerId_ownerPhone PRIMARY KEY (ownerId, ownerPhone),
	CONSTRAINT FK_OwnerPhone_ownerId FOREIGN KEY (ownerId)
			REFERENCES [Smurf.Owner] (ownerId)
			ON DELETE CASCADE ON UPDATE CASCADE);


CREATE TABLE [Smurf.Lease](
	leaseId CHAR(3) NOT NULL,
	leaseDuration int,
	leaseArea  DECIMAL(5,1),
	leasePrice DECIMAL(5,1),
	leaseBedroom int,
	leaseBathroom int,
	CONSTRAINT PK_Lease_leaseId PRIMARY KEY (leaseId),
)


CREATE TABLE [Smurf.Reviewer](
	reviewerId CHAR(3) NOT NULL,
	reviewerFName VARCHAR(50),
	reviewerMInit VARCHAR(20),
	reviewerLName VARCHAR(50),
	CONSTRAINT PK_Reviewer_reviewerId PRIMARY KEY (reviewerId),
)


CREATE TABLE [Smurf.Resident](
	residentId CHAR(3) NOT NULL,
	residentFName VARCHAR(20),
	residentMInit VARCHAR(20),
	residentLName VARCHAR(20),
	residentGender CHAR(1),
	residentAge int,
	residentCitizenShip VARCHAR(30),
	CONSTRAINT PK_Resident_residentId PRIMARY KEY (residentId),
)


CREATE TABLE [Smurf.Review](
	reviewId CHAR(3) NOT NULL,
	reviewRate DECIMAL(2,1),
	reviewContent VARCHAR(2500),
	CONSTRAINT PK_Review_reviewId PRIMARY KEY (reviewId),
)


CREATE TABLE [Smurf.Response](
	reviewId CHAR(3) NOT NULL,
	responseId CHAR(3) NOT NULL,
	responder VARCHAR(20),
	responseContent VARCHAR(2000),
	replyDate DATE
	CONSTRAINT PK_Response_reviewId_responseId PRIMARY KEY (reviewId, responseId),
	CONSTRAINT FK_Response_reviewId FOREIGN KEY (reviewId)
			REFERENCES [Smurf.Review] (reviewId)
			ON DELETE CASCADE ON UPDATE CASCADE);


CREATE TABLE [Smurf.Sign](
	residentId CHAR(3) NOT NULL,
	leaseId CHAR(3),
	unitId CHAR(3),
	signDate DATE
	CONSTRAINT PK_Sign_residentId PRIMARY KEY (residentId),
	CONSTRAINT FK_Sign_residentId FOREIGN KEY (residentId)		
			REFERENCES [Smurf.Resident] (residentId)
			ON DELETE NO ACTION ON UPDATE CASCADE,
	CONSTRAINT FK_Sign_leaseId FOREIGN KEY (leaseId)		
			REFERENCES [Smurf.Lease] (leaseId)
			ON DELETE NO ACTION ON UPDATE NO ACTION,
	CONSTRAINT FK_Sign_unitId FOREIGN KEY (unitId)		
			REFERENCES [Smurf.Unit] (unitId)
			ON DELETE NO ACTION ON UPDATE NO ACTION);


CREATE TABLE [Smurf.Comment](
	reviewId CHAR(3) NOT NULL,
	unitId CHAR(3),
	reviewerId CHAR(3),
	commentDate DATE
	CONSTRAINT PK_Comment_reviewId PRIMARY KEY (reviewId),
	CONSTRAINT FK_Comment_reviewId FOREIGN KEY (reviewId)		
			REFERENCES [Smurf.Review] (reviewId)
			ON DELETE CASCADE ON UPDATE CASCADE,
	CONSTRAINT FK_Comment_unitId FOREIGN KEY (unitId)		
			REFERENCES [Smurf.Unit] (unitId)
			ON DELETE CASCADE ON UPDATE CASCADE,
	CONSTRAINT FK_Comment_reviewerId FOREIGN KEY (reviewerId)		
			REFERENCES [Smurf.Reviewer] (reviewerId)
			ON DELETE CASCADE ON UPDATE CASCADE);
















	


 



	
