DROP VIEW IF EXISTS [Smurf.unitAvgRate]


GO
CREATE VIEW [Smurf.unitAvgRate] AS
	SELECT U.unitId, U.unitName, COUNT(DISTINCT(R.reviewId)) AS 'Review Numbers', SUM(R.reviewRate) / COUNT(R.reviewId) AS 'AvgRate'
	FROM [Smurf.Unit] U, [Smurf.Review] R, [Smurf.Comment] C
	WHERE U.unitId = C.unitId AND R.reviewId = C.reviewId
	GROUP BY U.unitId, U.unitName



